                <Beacon>
                </Beacon>
            </TitleContainer>
            <NavItemsContainer>
                <NavItem>
                    <NavBeacon />
                    RESEARCH
                </NavItem>
                <NavItem>
                    <NavBeacon />
                    AUSTIN-LAB
                </NavItem>
                <NavItem>
                    <NavBeacon />
                    NEWS
                </NavItem>
                <NavItem>
                    <NavBeacon />
                    TEACHING
                </NavItem>
            </NavItemsContainer>
            <DarkModeContainer>
                DARK MODE
            </DarkModeContainer>
        </NavContainer>
    )
}

const NavContainer = styled.div({
    border: "1px solid red",
