import styled from "styled-components"
import { useEffect, useState } from "react"
import { CustomParagraph, CustomTitle } from "../CustomText"
import Content from "../content.json"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faAt, faLocationDot, faPhone, faUserGraduate } from '@fortawesome/free-solid-svg-icons'

export const HomePage = () => {
    const [scroll, setScroll] = useState(0)

    const listenScrollEvent = (event) => {
        setScroll(window.scrollY)
    }
    useEffect(() => {
        window.addEventListener('scroll', listenScrollEvent);

        return () =>
            window.removeEventListener('scroll', listenScrollEvent);
    }, []);
    return (
        <>
            <Cover scroll={scroll} />
            <MiddleContainer>
                <Biography />
                <ContactInfo />
            </MiddleContainer>
            <RecentNews />

        </>

    )
}


const Cover = ({ scroll }) => {
    return (
        <>
            <CoverContainer>
                <JellyBean scroll={scroll}>

                    <svg xmlns='http://www.w3.org/2000/svg' height='100%' viewBox='0 0 3890.303 2752.572'>
                        <path id='Path_1' data-name='Path 1' d='M1698.446-70.623S1639.3,155.428,819.96,214.089-251.867,1318.881,966.628,1715.621s3015.725,52.321,3051.585-1302.1C4049.339-762.119,3006.728-1105.8,2320.371-747.287A1065.278,1065.278,0,0,0,2043.81-539.048C1882.614-292.806,1698.446-70.623,1698.446-70.623Z' transform='translate(-128.591 882.483)' fill='#FADFC6' />
                    </svg>
                </JellyBean>
                <BgName scroll={scroll}>
                    TODD AUSTIN
                </BgName>
                <Portrait>
                    <img src="./todd.png" alt="todd" width={"100%"} style={{ maxHeight: "800px" }} />
                </Portrait>
            </CoverContainer >

        </>

    )
}
const Biography = () => {
    return (
        <div style={{ width: "50%", marginRight: "15px" }}>

            <CustomParagraph title={"BIBLOGRAPHY"}>
                {Content.personal_biography}
            </CustomParagraph>
        </div>


    )
}
const ContactInfo = () => {
    return (
        <InfoContainer>
            <CustomParagraph title={"BASIC INFORMATION"} >

                <b>
                    Todd Austin
                </b>
                <br />
                Professor, EECS, University of Michigan
            </CustomParagraph>
            <AddressContainer>
                <AddressItems>
                    <AddressIcons>
                        <FontAwesomeIcon icon={faLocationDot} size="lg" color="#ff7055" />
                    </AddressIcons>
                    <div>
                        4637 BBB
                        <br />
                        2260 Hayward Avenue
                        <br />
                        Ann Arbor, MI  48109-2121
                    </div>
                </AddressItems>
                <AddressItems>
                    <AddressIcons>
                        <FontAwesomeIcon icon={faPhone} size="lg" color="#ff7055" />
                    </AddressIcons>
                    <div>
                        734-936-0370
                    </div>
                </AddressItems>
                <AddressItems>
                    <AddressIcons>
                        <FontAwesomeIcon icon={faAt} size="lg" color="#ff7055" />
                    </AddressIcons>
                    <div>
                        austin@umich.edu
                    </div>
                </AddressItems>

            </AddressContainer>

            <SocialContainer>


                google scholar
                linkedin
                twitter
                youtube
                github
                <FontAwesomeIcon icon={faUserGraduate} />
            </SocialContainer>

        </InfoContainer>
    )
}
const RecentNews = () => {


    return (
        <div style={{ margin: "50px 0px", }}>
            <CustomTitle title={"RECENT NEWS"} />
            <hr />

            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:share:6954184073858678785" height="832px" width="504px" frameBorder="0" allowFullScreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:share:6947948017572339712" height="1300" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:share:6928336091674525696" height="792" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:6922642580803391488" height="730" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:share:6920036081232928769" height="1012" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:share:6911671103392219136" height="974" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:6904068668544925696" height="710" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
            <div>
                <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:ugcPost:6888503943756824576" height="870" width="504" frameborder="0" allowfullscreen="" title="Embedded post"></iframe>
            </div>
        </div >
    )
}




// styled components ------------------------------------------------------------------
const InfoContainer = styled.div({
    // border: "1px solid red",
    fontFamily: "Gotham",
    color: "#505050",
    lineHeight: "24px",
    width: "30%",
    // height: 800,
    // backgroundColor: "#edf1f9",
    // paddingTop: 50,
    // display: "flex",
    // flexDirection: "column",
    // justifyContent: "center",
    // alignItems: "center",
})
const AddressContainer = styled.div({
    border: "1px solid green",

})
const AddressItems = styled.div({
    // border: "1px solid green",
    margin: "10px 0px",

    display: "flex",
