import styled from "styled-components"

export const CustomText = ({ title, children }) => {
    let content = (children.length > 200) ? children.slice(0, 200) + " ..." : children;


    return (
        <CustomDiv>
            <Header>
                <Beacon />
                <CustomTitle>{title}</CustomTitle>
            </Header>
            <Body>
                {children.length < 200 && children}
                {children.length > 200 && children.slice(0, 200) + " ..."}
                {children.length > 200 && <div>READ MORE</div>}

            </Body>
        </CustomDiv>
    )
}

const CustomDiv = styled('div')`
border:1px solid red;
padding: 15px 15px;
`
const CustomTitle = styled('div')`
font-size: 100%;
font-family: Gotham;
font-weight: 500;
`

const Header = styled('div')`
// border:1px solid red;
display: flex;
margin-bottom: 20px;
flex-direction: row;
justify-content: start;
align-items: start;
`
const Beacon = styled('div')`
    background-color: #ff7055;
    height: 13px;
    width: 4px;
    margin-right: 5px
`
const Body = styled('div')`
font-family: Gotham;
color: #707070;
line-height: 24px;


`
